const { initializeWebServer } = require("./entry-points/api");
const { MessageQueueStarter } = require("./entry-points/message-queue-starter");

async function start() {
  await initializeWebServer();
  await new MessageQueueStarter().start();
}

start()
  .then(() => {
    console.log("The app has started successfully");
  })
  .catch((error) => {
    console.log("App occured during startup", error);
  });

const isPortReachable = require("is-port-reachable");
const path = require("path");
const dockerCompose = require("docker-compose");
const { execSync } = require("child_process");

module.exports = async () => {
  console.time("global-setup");

  // Ô∏èÔ∏èÔ∏è‚úÖ Best Practice: Speed up during development, if already live then do nothing
  const isDBReachable = await isPortReachable(54310);
  if (!isDBReachable) {
    // Ô∏èÔ∏èÔ∏è‚úÖ Best Practice: Start the infrastructure within a test hook - No failures occur because the DB is down
    await dockerCompose.upAll({
      cwd: path.join(__dirname),
      log: true,
    });

    await dockerCompose.exec(
      "database",
      ["sh", "-c", "until pg_isready ; do sleep 1; done"],
      {
        cwd: path.join(__dirname),
      }
    );

     // Ô∏èÔ∏èÔ∏è‚úÖ Best Practice: Use npm script for data seeding and migrations
    execSync("npm run db:migrate");
    // ‚úÖ Best Practice: Seed only metadata and not test record, read "Dealing with data" section for further information
    execSync("npm run db:seed");
  }

  // üëçüèº We're ready
  console.timeEnd("global-setup");
};

const isCI = require("is-ci");
const dockerCompose = require("docker-compose");
const OrderRepository = require("../data-access/order-repository").default;

module.exports = async () => {
  if (isCI) {
    // Ô∏èÔ∏èÔ∏è‚úÖ Best Practice: Leave the DB up in dev environment
    dockerCompose.down();
  } else {
    // ‚úÖ Best Practice: Clean the database occasionally
    if (Math.ceil(Math.random() * 10) === 10) {
      await new OrderRepository().cleanup();
    }
  }
};
